    render() { 
        return (
            <div>
            <button className="btn-info btn-sm" 
                onClick={this.handleReset}
            >Reset</button>
            {this.state.counters.map(
                counter => <Counter onDelete={this.handleDelete} counter={counter}/>
            )}
            </div>
          );
    }
}

export default Counters;
